            -ms-transform: translate(-50%, -50%);
            h1 {
                color: white;
                font-weight: 100;
                font-size: 32px;
                line-height: 48px;
                width: 775px;
                text-align: justify;
                a {
                    cursor: pointer;
                    position: relative;
                    color: white;
                    span {
                        position: absolute;
                        display: inline-block;
                        height: 1px;
                        width: 100%;
                        background: white;
                        bottom: -5px;
                    }
                }
            }
        }
        .normal-device {
            @media (max-width: 860px) {
                display: none;
